from machine import Pin, SoftI2C
from ssd1306 import SSD1306_I2C



i2c = SoftI2C(sda=Pin(21), scl=Pin(22))
oled = SSD1306_I2C(128, 64, i2c, addr=0x3c)

def display_image_fast(oled, img):
    oled.fill(0)
    buffer = oled.buffer
    img_len = len(img)
    buffer_len = len(buffer)
    
    # 确保我们不会超出缓冲区的范围
    copy_len = min(img_len, buffer_len)
    
    # 使用bytearray提高兼容性
    if isinstance(img, (bytes, bytearray)):
        buffer[:copy_len] = img[:copy_len]
    else:
        # 如果img不是字节类型，我们逐字节复制
        for i in range(copy_len):
            buffer[i] = img[i] & 0xFF  # 确保每个值都是一个字节
    
    oled.show()
    
    
def display_image(oled,img):
    oled.fill(0)
    oled_width = 128
    oled_height = 64
    # 将图像数据显示到OLED上
    for y in range(oled_height // 8):
        for x in range(oled_width):
            oled.buffer[y * oled_width + x] = img[y * oled_width + x]

    # 刷新显示屏
    oled.show()
    
